prefix=''
suffix=''

prefixes = [ prefix[ 3*i:3*i+3 ] for i in range( len( prefix ) // 3 ) ]
suffixes = [ suffix[ 3*i:3*i+3 ] for i in range( len( prefix ) // 3 ) ]

################################################################################

from svglib.svglib import svg2rlg
from reportlab.graphics import renderPDF, renderPM

from glob import glob
dirs = glob('svg/*')

for dir in dirs:
    size = int(dir[4:])
    for fix in prefixes+suffixes:
        drawing = svg2rlg(f'svg/{size}/{fix}.svg')
        renderPM.drawToFile(drawing,f"png/{size}/{fix}.png",fmt="PNG")
